package Assignments;
//Sum of first 10 natural numbers
public class AssigS3Q2{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1,sum=0;
		while(n<=10)
		{
			sum=sum+n;
			n++;
	}
			
		 System.out.println(" The Sum of first 10 natural numbers is " +sum);
	}

	}


