package SeleniumPrerequisiteAssignment;
//swap two numbers with out using third variable
public class Q4  {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=20;
    int b=40;
    a=a+b;//a=20+40=60
    b=a-b;//b=60-40=20
    a=a-b;//a=60-20=40
    System.out.println("a="+a);
    System.out.println("b="+b);
	}
}
	

